mapper.update:
UPDATE article 
SET view_count = view_count + 1 
WHERE id = #{id}

UPDATE article 
SET like_count = like_count + 1 
WHERE id = #{id}

UPDATE article 
SET like_count = GREATEST(like_count - 1, 0) 
WHERE id = #{id}

UPDATE article 
SET comment_count = comment_count + 1 
WHERE id = #{id}

UPDATE article 
SET comment_count = GREATEST(comment_count - 1, 0) 
WHERE id = #{id}
